import { 
    SEND_ORDER,
    UPDATE_ORDER,
    GET_ORDERS,
    ALL_ORDERS
} from '../constants/constant'
import isEmpty from 'lodash/isEmpty'

const initialState = {
    msg: null,
    allOrder: null,
    msg: "",
    admin: null
};

const order = (state= initialState, action) => {
    switch(action.type){
        case SEND_ORDER:
            return {
                ...state,
                msg: action.payload
            }
        case GET_ORDERS: 
            return {
                ...state,
                allOrder: action.payload,
            }
        case UPDATE_ORDER:
            return {
                ...state,
                allOrder: action.payload,
                msg: action.payload
            }
        case ALL_ORDERS:
            return {
                ...state,
                admin: action.payload
            }
        default: return state;
    }
}

export default order;
