import React, {useState} from 'react'
import { Jumbotron, Button, Row, Col, Form, Card, Modal } from 'react-bootstrap';
import { faApple, faAndroid } from '@fortawesome/free-brands-svg-icons'
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome'
import { faTimes} from '@fortawesome/free-solid-svg-icons'
import img from '../images/home.png'
import { Application } from '../components/export';
import Bill from './Bill';
import Detail from './Detail';

const Listing = (props) => {
    const [display, setDisplay] = useState("none");
    const [contact, setContact] = useState();
    const [message, setMessage] = useState('');
    const [show, setShow] = useState(false);
    const handleClose = () => setShow(false);
    const handleShow = () => setShow(true);

    const toggleDisplay = () => {
        if(display === "none"){
            setDisplay("display")
        }else{
            setDisplay("none")
        }   
    }

    const choice = async (value) => {
        setContact(value);
        if(display === "none"){
            setDisplay("display")
        }else{
            setDisplay("none")
        }
    }

    const item = props.value;
    return (
        <>
        <Col md={4}>
            <Card className="text-center" style={{marginBottom: "10px"}}>
                <Card.Header>{item.type}</Card.Header>
                <Card.Body>
                    <Card.Title>{item.transaction_type}</Card.Title>
                    <Card.Text>
                    Amount of bitcoin: 
                    <p><strong>{item.btc_quantity}</strong></p>
                    </Card.Text>
                    <Card.Text>
                    Price: 
                    <p><strong>{item.price}</strong></p>
                    </Card.Text>
                    <Button variant="primary" onClick={handleShow}>
                        Edit
                    </Button>
                    <Modal show={show} onHide={handleClose}>
                        <Modal.Header closeButton>
                        <Modal.Title>Bill Detail</Modal.Title>
                        </Modal.Header>
                        <Modal.Body>
                            {console.log("item",item)}
                            <Detail type={item.type} detail={item}/>
                        </Modal.Body>
                    </Modal>
                </Card.Body>
            </Card>
        </Col>
        </>
    )
}

export default Listing
