import React, {useState} from 'react'
import { Tabs, Tab, Row, Table } from 'react-bootstrap';
import { faApple, faAndroid } from '@fortawesome/free-brands-svg-icons'
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome'
import Sell from './Sell'
import Buy from './Buy'
import Cancelled from './Cancelled';
import Countup from 'react-countup'
import Fulfilled from './Fulfilled';

class Home extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            key: 'Buy'
        }
    }
    async componentDidMount() {
        try {
            console.log("componentDidMount");
            // setInterval(async () => {
            //     console.log("refreshing");
            //     const { data } = await axios.get('http://localhost:9000/marketprice');
            //     localStorage.setItem('price', data.CURRENT_PRICE)
            // }, 10000);
        } catch(e) {
        console.log(e);
        }
    }
    render() {
    return (
        <div style={{width: "60%", margin: "0 auto"}}>
            {/* <h6>Market Price: ${localStorage.getItem('price')}</h6> */}
            <p>Real Time</p>
            <h6 style={{color: "red"}}>Latest Ask Price: <Countup start={40000} end={localStorage.getItem('price')} duration={1} prefix='$' decimals={2}/></h6>
            <h6 style={{color: "green"}}>Bid price: <Countup start={40000} end={localStorage.getItem('price')} duration={1} prefix='$' decimals={2}/></h6>
            <h6 style={{color: "blue"}}>Latest Transaction Price: <Countup start={40000} end={localStorage.getItem('price')} duration={1} prefix='$' decimals={2}/></h6>

            {/* You must be able to show the latest ask price and bid price. Ideally, this should be refreshed in real-time in the UI. This is available to all users. */}

            <Tabs
            id="controlled-tab-example"
            activeKey={this.state.key}
            onSelect={(k) => this.setState({key: k})}
            style={{margin: "auto 0"}}
            >
                <Tab eventKey="Buy" title="Buy">
                    <Buy />
                </Tab>
                <Tab eventKey="Sell" title="Sell">
                    <Sell />
                </Tab>
                <Tab eventKey="Cancelled" title="Cancelled">
                    <Cancelled />
                </Tab>
                <Tab eventKey="Fulfilled" title="Fulfilled">
                    <Fulfilled />
                </Tab>
            </Tabs>
        </div>
    )
    }
}


export default Home
