import React, {useState, useEffect} from 'react'
import { Jumbotron, Button, Row, Col, Form, Card, Modal } from 'react-bootstrap';
import { faApple, faAndroid } from '@fortawesome/free-brands-svg-icons'
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome'
import { faTimes} from '@fortawesome/free-solid-svg-icons'
import img from '../images/home.png'
import { Application } from '../components/export';
import Listing from '../components/Listing';
import Order from "../components/Order"
import { useSelector } from 'react-redux';
const Buy = () => {
    const allOrder = useSelector((state) => state.order ? state.order.allOrder : null)

    const [display, setDisplay] = useState("none");
    const [contact, setContact] = useState();
    const [message, setMessage] = useState('');
    const [orders, setOrders] = useState('');

    const [show, setShow] = useState(false);
    const handleClose = () => setShow(false);
    const handleShow = () => setShow(true);

    useEffect(() => {
        if(allOrder)
            setOrders(allOrder)
    }, [allOrder])

    const toggleDisplay = () => {
        if(display === "none"){
            setDisplay("display")
        }else{
            setDisplay("none")
        }
    }

    const choice = async (value) => {
        setContact(value);
        if(display === "none"){
            setDisplay("display")
        }else{
            setDisplay("none")
        }
    }

    let lists = allOrder ? allOrder.filter((l) => l.type === "BUY" && l.status === "Open") : null
    return (
        <>
        <Order type="BUY" />
        <div style={{margin: "0 auto"}}>
            <h3 style={{textAlign: "center"}}>Buy History</h3>
            {lists && lists.length === 0 ? <div>There is no buy order available</div>
            :
             <Row>
                {lists && lists.map((item, index) => {
                    return (
                        <Listing key={index} value={item}/>
                    )
                })}
            </Row>
            }
            {!lists ? <div>There is no sell order available</div> : null}
        </div>
        </>
    )
}

export default Buy
