import React, {useEffect, useState} from 'react'
import { Button, Form, Col, Row, Table } from 'react-bootstrap';
import { faPen, faTimes } from '@fortawesome/free-solid-svg-icons'
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome'
import Application from '../components/application/applicaiton'
import { useDispatch, useSelector } from 'react-redux';
import FileBase from 'react-file-base64';
import logo from '../images/signup.png'
import {update, updateBalance} from '../actions/authActions'
import { getUsers } from '../actions/authActions'
import {USD_RMB, USD_EUR, USD_GBP, USD_INR, BIT_USD} from '../constants/constant'

const Profile = () => {
    const user = useSelector((state) => state.auth ? state.auth.user : null)
    const allBalance = useSelector((state) => state.auth ? state.auth.balance : null)
    const [balance, setBalance] = useState()
    const [bitCoin, setBitCoin] = useState()

    useEffect(() => {
        if(allBalance)
            setBalance(allBalance)
    }, [allBalance])

    const dispatch = useDispatch()

    const [balanceInfo, setBalanceInfo] = useState({
        username: user.username,
        currency: user.bank? user.bank.currency.toUpperCase() : "EUR",
        operation: "deposite",
        amount: ""
    })

    // const onSubmit = async (e) => {
    //     e.preventDefault();
    //     console.log("balanceInfo", balanceInfo);
    //     console.log("submit bank");
    //     // const addBank = {bank: bankInfo, address: addressInfo}
    //     // dispatch(addBank(bankInfo))
    // }

    const currencies = ["EUR", "USD", "GBP", "INR", "RMB"]
    let currencyOption = currencies.map((item, index) => {
        return <option value={item} key={index}>{item}</option>
    });

    const handleChange = event => {
        const target = event.target;
        const value = target.type === 'checkbox' ? target.checked : target.value;
        setBalanceInfo({...balanceInfo, currency: value})
    }

    const action = async (input) => {
        balanceInfo["operation"] = input
        console.log("balanceInfo", balanceInfo, input);
        await dispatch(updateBalance(balanceInfo))
        setBalanceInfo({...balanceInfo, operation: "", amount: ""})
    } 

    const actionWithBit = async (input) => {
        balanceInfo["operation"] = input
        let initializeCurrency = balanceInfo.currency
        balanceInfo['amount'] = bitCoin
        balanceInfo["currency"] = "Btc"
        console.log("balanceInfo", balanceInfo, input);
        await dispatch(updateBalance(balanceInfo))
        setBalanceInfo({...balanceInfo, operation: "", amount: "", currency: initializeCurrency})
    } 



    return (
        <>
        <div style={{width: "60%", margin: "0 auto"}}>
            <Row className="justify-content-center">
                <h4>User Profile</h4>
            </Row>
            <h6>Default Currency: <strong>{user.bank? user.bank.currency : "NONE"}</strong></h6>
            {/* <h6>Bank Account Balance: <strong>{balance ? balance.}</strong></h6> */}

            <div style={{width: "50%"}}>
                <Row>
                    <Col>
                    <div className="form-group">
                        <label htmlFor="currency">Currency</label>
                        <select
                            name="currency"
                            id="currency"
                            value={balanceInfo.currency}
                            className="form-control"
                            onChange={handleChange}>
                            {currencyOption}
                        </select>
                    </div>
                    </Col>
                    <Col>
                    <Form.Group controlId="formAmount">
                        <Form.Label>Amount</Form.Label>
                        <Form.Control value={balanceInfo.amount} onChange={(e) => setBalanceInfo({ ...balanceInfo, amount: e.target.value })} type="number"  required />
                    </Form.Group>
                    </Col>
                </Row>
                <Button variant="primary" value="withdraw" onClick={(e) => action(e.target.value)}>
                    Withdraw
                </Button>
                <Button variant="danger" value="deposite" onClick={(e) => action(e.target.value)} style={{marginLeft: "10px"}}>
                    Deposite
                </Button>
                <h6>BitCoin Balance: <strong>{balance && balance.btc ? balance.btc : 0}</strong></h6>
                <Form.Group controlId="formBitcoin">
                    <Form.Label>BitCoin</Form.Label>
                    <Form.Control value={bitCoin} onChange={(e) => setBitCoin(e.target.value)} type="number"  required />
                </Form.Group>
                <Button variant="primary" value="withdraw" onClick={(e) => actionWithBit(e.target.value)}>
                    Withdraw
                </Button>
                <Button variant="danger" value="withdraw" onClick={(e) => actionWithBit(e.target.value)} style={{marginLeft: "10px"}}>
                    Deposite
                </Button>
            </div>
            <p></p>
            <h6>Bank Account Balance In Different Currency Type:</h6>
            <Row>
                <Table striped bordered hover>
                    <thead>
                        <tr>
                        <th>Currency</th>
                        <th>Balance</th>
                        </tr>
                    </thead>
                    <tbody>
                        <tr>
                            <td>USD</td>
                            <td>{balance && balance.usd ? balance.usd : 0}</td>
                        </tr>
                        <tr>
                            <td>CNY</td>
                            <td>{balance && balance.cny ? balance.cny : 0}</td>
                        </tr>
                        <tr>
                            <td>INR</td>
                            <td>{balance && balance.inr ? balance.inr : 0}</td>
                        </tr>
                        <tr>
                            <td>EUR</td>
                            <td>{balance && balance.eur ? balance.eur : 0}</td>
                        </tr>
                        <tr>
                            <td>GBP</td>
                            <td>{balance && balance.gbp ? balance.gbp : 0}</td>
                        </tr>
                        <tr>
                            <td>BitCoin</td>
                            <td>{balance && balance.btc ? balance.btc : 0}</td>
                        </tr>
                    </tbody>
                </Table>
            </Row>
        </div>
        </>
    )
}

export default Profile
