import React, { useState } from 'react'
import { useEffect } from 'react';
import { Tabs, Tab, Row, ListGroup} from 'react-bootstrap';
import { useDispatch, useSelector } from 'react-redux';
import { addBank } from '../actions/authActions';

const Report = () => {
    const allTransactions = useSelector((state) => state.order ? state.order.admin : null)
    const user = useSelector((state) => state.auth ? state.auth.user : null)

    // let transactions = [{id: 1, user: "laozhang@gmail.com", start: "time1", end: "time2", type: "Limit Order", balance: 102.11, paid: "paid", fee: 1.12, bitcoin: 12.21, price: 23213}, {id: 2, user: "yoho4@gmail.com", start: "time1", end: "time2", type: "Limit Order", balance: 1002.11, paid: "paid", fee: 0.12, bitcoin: 102.2, price: 23232113}, {id: 3, user: "lin", start: "time1", end: "time2", type: "Market Order", balance: 10.32, paid: "sent", fee: 2.12, bitcoin: 3.21, price: 315}, {id: 4, user: "laozhang@gmail.com", start: "time1", end: "time2", type: "Market Order", balance: 43.11, paid: "sent", fee: 1.12, bitcoin: 23, price: 456}]

    const [key,setKey] = useState('user')
    const [filtered, setFiltered] = useState([])
    const [transactions,setTransactions] = useState()

    useEffect(() => {
        if(allTransactions)
            setTransactions(allTransactions)
    }, [allTransactions])

    const setSection = (input) => {
        if(input === "user"){
            console.log("inside filter", input);
            const after = transactions.filter((t) => t.username === user.username)
            console.log("after", after);
            setFiltered(after)
        }
        setKey(input)
    }

    return (
        <> 
        <div style={{width: "60%", margin: "0 auto"}}>
            <Tabs
            id="controlled-tab-example"
            activeKey={key}
            onSelect={(k) => setSection(k)}
            style={{margin: "auto 0"}}
            >
                <Tab eventKey="user" title="user">
                    <div style={{margin: "0 auto", width: "70%"}}>
                        <Row className="justify-content-center">
                            <h4>User Report</h4>
                        </Row>
                        <ListGroup>
                            {filtered ? filtered.map((item, index) => {
                                // console.log("item", item);
                                // // return (
                                // //     <ListGroup.Item>
                                // //         {item.user} time: {item.start} - {item.end}<br/>
                                // //         <h6>type: {item.type}</h6>
                                // //         balance: {item.balance}<br/>
                                // //         status: {item.paid}<br/>
                                // //         bitcoin: {item.bitcoin}<br/>
                                // //         price: {item.price}<br/>
                                // //         amount: {item.fee}
                                // //     </ListGroup.Item>
                                // // )
                                return (
                                    <ListGroup.Item>
                                        {item.username} 
                                        {item.start_date}<br/>
                                        <h6>type: {item.type}</h6>
                                        order: {item.transaction_type}<br/>
                                        currency: {item.currency}<br/>
                                        bitcoin: {item.btc_quantity}<br/>
                                        price: {item.price}<br/>
                                        status: {item.status}<br/>
                                    </ListGroup.Item>
                                )
                            }): null}
                        </ListGroup>
                    </div>
                </Tab>
                <Tab eventKey="system" title="system">
                    <div style={{margin: "0 auto", width: "70%"}}>
                        <Row className="justify-content-center">
                            <h4>System Report</h4>
                        </Row>
                        <ListGroup>
                            {transactions ? transactions.map((item, index) => {
                                // console.log("item", item);
                                return (
                                    <ListGroup.Item>
                                        {item.username} 
                                        {item.start_date}<br/>
                                        <h6>type: {item.type}</h6>
                                        order: {item.transaction_type}<br/>
                                        currency: {item.currency}<br/>
                                        bitcoin: {item.btc_quantity}<br/>
                                        price: {item.price}<br/>
                                        status: {item.status}<br/>
                                    </ListGroup.Item>
                                )
                            }): null}
                        </ListGroup>
                    </div>
                </Tab>
            </Tabs>
        </div>
        {/* <div style={{margin: "0 auto", width: "50%"}}>
            <Row className="justify-content-center">
                {admin ? <h4>User Report</h4> : <h4>System Report</h4>}
            </Row>
            <ListGroup>
                {transaction.map((item, index) => {
                    return (
                        <ListGroup.Item>
                            {item.user} time: {item.start} - {item.end}<br/>
                            bitcoin: {item.bitcoin}<br/>
                            price: {item.price}
                        </ListGroup.Item>
                    )
                })}
            </ListGroup>
        </div> */}
        </>
    )
}

export default Report
