import React, {useState} from 'react'
import { Tabs, Tab, Row } from 'react-bootstrap';
import { faApple, faAndroid } from '@fortawesome/free-brands-svg-icons'
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome'
import img from '../images/home.png'
import Sell from './Sell'
import Buy from './Buy'
import axios from 'axios';

class Home extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            key: 'buy'
        }
    }
    async componentDidMount() {
        try {
            console.log("componentDidMount");
            setInterval(async () => {
                console.log("refreshing");
                const { data } = await axios.get('http://localhost:9000/marketprice');
                localStorage.setItem('price', data.CURRENT_PRICE)
            }, 10000);
        } catch(e) {
        console.log(e);
        }
    }
    render() {
    return (
        <div style={{width: "60%", margin: "0 auto"}}>
            <h6>Market Price: ${localStorage.getItem('price')}</h6>
            <h6>Latest Ask Price: ${localStorage.getItem('price')}</h6>
            <h6>Bid price: ${localStorage.getItem('price')}</h6>

            {/* You must be able to show the latest ask price and bid price. Ideally, this should be refreshed in real-time in the UI. This is available to all users. */}

            <Tabs
            id="controlled-tab-example"
            activeKey={this.state.key}
            onSelect={(k) => this.setState({key: k})}
            style={{margin: "auto 0"}}
            >
                <Tab eventKey="buy" title="buy">
                    <Buy />
                </Tab>
                <Tab eventKey="sell" title="sell">
                    <Sell />
                </Tab>
            </Tabs>
        </div>
    )
    }
}


export default Home
